<?phpnamespace Home\Controller;use Common\Controller\AdminbaseController;class AdminPayMngController extends AdminbaseController {        protected $Dao;        function _initialize() {                parent::_initialize();        $this->Dao = D("Home/Bonus");    }        function index(){                $managerList = $this->Dao            ->join('sd_perf ON sd_perf.id = sd_bonus.pid')            ->join('sd_broker BB ON BB.id = sd_perf.bid')            ->join('sd_broker MB ON MB.id = sd_bonus.sid')            ->join('sd_project ON sd_project.id = sd_perf.pid')            ->join('sd_store ON sd_store.manager_id = sd_bonus.sid', 'left')            ->field("MB.name AS mname, DATE_FORMAT(sd_perf.date,'%Y-%m') AS date, BB.name AS bname, sd_project.name AS pname, SUM(sd_bonus.bonus) AS bonus, sd_store.name AS sname")            ->group('1,2,3,4')            ->select();                $this->assign('managerList', $managerList);                $this->display();    }}