<?phpnamespace Home\Controller;use Common\Controller\AdminbaseController;class AdminPerfReportController extends AdminbaseController {        protected $Dao;        function _initialize() {                parent::_initialize();        $this->Dao = D("Home/Store");    }        function index(){                if(IS_POST) {                    }                $storeList = $this->Dao            ->join('sd_broker ON sd_broker.id = sd_store.manager_id')            ->field('sd_broker.id, sd_store.name AS sname, sd_broker.name AS bname')            ->select();        $date = '2015-05';        foreach ($storeList as &$s) {            $s['date'] = $date;            $s['perf'] = getSelfStoreProfit($s['id'], $s['date']);        }        $this->assign('storeList', $storeList);                $this->display();    }}