<?phpnamespace Home\Controller;use Common\Controller\AdminbaseController;class AdminPerfYearController extends AdminbaseController {        function _initialize() {        parent::_initialize();    }        function index() {    	$yearList = array();    	for($i=0; $i<10; $i++) {    		$yearList[] = date("Y",strtotime("-$i year"));    	}    	    	    	$this->assign('yearList', $yearList);        $this->display();    }}